import React from 'react';
import './index.css';


 function App2(){
  let date = new Date();
  date = date.getHours();
  let greet='';
  const styleCss={};
 
 
  if (date>=1 && date<12) {
    greet='good morning';
    styleCss.color='green';
 
  }else if(date>=12 && date<19){
    greet='good afternoon';
  }else{
    greet='good night';
    styleCss.color='red';
  }
     return(<>
     <div>
        <h1>Hello  SSir, <span style={styleCss}>{greet}</span></h1>
        </div>
        </>
     );
 }
 export default App2;
