import React, { useState } from 'react';

const App=()=>{

    const [name,setName]=useState();
    let inputEvent=(e)=>{
        setName()
    }


    return(
        <>
            <h1>welcome {name}</h1>
            <input type='text' placeholder='enter your name'
            onChange={inputEvent} />

        </>
    )
}
export default App;
