import React, { useState } from 'react';

const App=()=>{
    const [btn,setBtn]=useState(0);
    const [bt,setBt]=useState(0);
    use 
    const btn1=()=>{
        setBtn(btn+1)
    }
    const btn2=()=>{
        setBt(bt+1)
    }

    return(<>

        <button onClick={btn1}>click me {btn}</button>
        <button onClick={btn2}>click me {bt}</button>
    </>)
}
export default App;
