import React from 'react';

const App=()=>{
    const [name,addName]=useState('s');
    let inputEvent=(event)=>{
    addName(event.target.value);
}


  return(  <>
        <div>


            <h1>Hello {name}</h1>
            <input type='text' placeholder='enter your name' 
                onChange={inputEvent}
            />
            <button>click me</button>
        </div>


    </>)
}

export default App;
