import React, { useState } from 'react';

const App=()=>{
     const purple='purple';
   
      const [bg, setBg]=useState(purple);
      const [name,setName]=useState("clickme");

      
    const bgChange=()=>{
       let newbg='green';
       setBg(newbg);
        setName("ouch!!");
    }

    const bgBack=()=>{
        const purple='purple';
        setBg(newbg);
    }

   return( 
   <>
    <div style={{backgroundColor: bg}}>
        <button onClick={bgChange} onDoubleClick={bgBack}>{name}</button>
    </div>
    </>

   )
}
export default App;
