import React, { useState } from 'react';
import DeleteIcon from '@material-ui/icons/Delete';

const List=(props)=>{
    const[line,setLine]= useState(false);
    const cutIt=()=>{
         
         setLine(true);
    };
    return(
        <>  <div>
            <span onClick={cutIt}><DeleteIcon /></span>
            <li style= {{textDecoration: line ? "line-through" : "none"}}
            >{props.text}</li>
            </div>
        </>
    )
}
export default List;
