import React from 'react';

import ReactDom from 'react-dom';
import './index.css';

let date = new Date();
date = date.getHours();
let greet='';
const styleCss={};


if (date>=1 && date<12) {
  greet='good morning';
  styleCss.color='green';

}else if(date>=12 && date<19){
  greet='good afternoon';
}else{
  greet='good night';
  styleCss='red';
}

ReactDom.render(
  <>
  <div>
  <h1>Hello Sir, <span style={styleCss}>{greet}</span></h1>
  </div>
  </>,
  document.getElementById('root')
);
