import React, { useState } from 'react';

const App=()=>{
    const [inputList,setInputList]= useState("buy Apple");

    const itemEvent=(event)=>{
        setInputList(e)
    }

    return(
        <>
            
            <h1>TODO list</h1>
            <br />
            <input type='text'
                placeholder='Add a item'
                onChange={itemEvent} />
                <button>+</button>

                <ol>
                    <li>{inputList}</li>
                   
                </ol>

           
        </>
    )
}
export default App;
