import React, { useState }  from 'react';

const ToDoList =()=>{
    const [num,setNum]= useState(0);
    const incNum = () =>{

        setNum(num+1)
    }
    const decNum = () =>{
        if(num>0){
        setNum(num-1)}
        else{
            alert('0 limit reached')
            setNum(0);
        }
    }

    return(
        <>
            <div className='main'>
            <div className='center'>
                <h1>{num}</h1>
                <div className='btn'>
                    <button onClick={incNum}>increment</button>
                    <button onClick={decNum}>decrement</button>
                </div>
            </div>
            </div>
        </>
    )
}
export default ToDoList;
