import { useState } from "react";

const App=()=>{

    const [name,setName]=useState({
        fname:'',
        lname:''
    })

    const newName=(prev)=>{
        const value=prev.target.value;
        const name=prev.target.name;

        setName((preValue)=>{
            if( name==='fname'){
                 return{
                     fname: value,
                 lname: preValue.lname
                 }
            }
        })
    }

    const onSubmit=(e)=>{
        e.preventDefault();
        alert('form submitted');
    }


    return(
        <>
            <h1>welcome{name.fname}{name.lname}</h1>
            <form onSubmit={onSubmit}>
                <input type='text' placeholder='enter your name' 
                name='fname'
                onChange={newName}
                />

            <br></br>
            <br></br>
                <input type='text' placeholder='enter your name' 
                name='lname'
                onChange={newName}
                />

            <br></br>
            <br></br>
                <button type='submit'>CLICK ME</button>
            </form>

        </>
    )
}
export default App;
