import React, { useState } from 'react';

const App=()=>{

    const [name,setName]=useState();
    let inputEvent=(event)=>{
        
        setName(event.target.value)
    }

    let on


    return(
        <>
            <h1>welcome {name}</h1>
            <input type='text' placeholder='enter your name'
            onChange={inputEvent} />

            <button onClick={onSubmit}>click here</button>
        </>
    )
}
export default App;
